#!/usr/bin/env python3


from random import choice, sample

class clothes:
	def __init__(self, unisexList = [], masculineList = [], feminineList = [], sillyList = []):
		self.unisexList = unisexList
		self.masculineList = masculineList
		self.feminineList = feminineList
		self.sillyList = sillyList
	def unisex(self):
		return self.unisexList.copy()
	def masculine(self):
		return self.masculineList.copy() + self.unisexList.copy()
	def feminine(self):
		return self.feminineList.copy() + self.unisexList.copy()
	def cross(self):
		return self.feminineList.copy() + self.masculineList.copy() + self.unisexList.copy()
	def silly(self):
		return self.sillyList.copy()
	#Took some searching for the right functions, but simplified returning all lists as one list into one line of code.
	def everything(self):
		return sum(list(vars(self).values()), [])
		
	#Optional weather additions.
	def addWeatherClothes(self, winterList = [], summerList = []):
		self.winterList = winterList
		self.summerList = summerList
	def winter(self):
		return self.winterList.copy()
	def summer(self):
		return self.summerList.copy()
	
#If I just want one list, with maybe a silly variety.
class simpleList:
	def __init__(self, theList = [], sillyList = []):
		self.theList = theList
		self.sillyList = sillyList
	def getList(self):
		return self.theList.copy()
	def getSilly(self):
		return self.sillyList.copy()
	def everything(self):
		return sum(list(vars(self).values()), [])
	def onlySome(self, runTimes = 1):
		#A function for returning fewer items. Theses lists may get so large returning random items I need will be easier on the system.
		return sample(self.theList, runTimes)

#For lists that change based on the scale requested.
class sizedList:
	def __init__(self, smallList = [], mediumList = [], largeList = []):
		self.smallList = smallList
		self.mediumList = mediumList
		self.largeList = largeList
	def small(self):
		return self.smallList.copy()
	def medium(self):
		return self.mediumList.copy()
	def large(self): 
		return self.largeList.copy()
	def everything(self):
		return sum(list(vars(self).values()), [])
		


	










